import React from 'react';
import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Modal from '@material-ui/core/Modal';
import axios from 'axios'
import { flexbox } from '@material-ui/system';

export default class SimpleModal extends React.Component {
    constructor(props){ 
        super(props) 
        this.state = {
        name : '',
        type : '',
        price: '',
        rating : '',
        years : '',
        available : false,
        showModal : false,
     }
    }
    handleChange = (e) => {
      e.preventDefault();
      this.setState({
        [e.target.name]: e.target.value
      })
    }

     handleOpen = () => {
     this.setState({
      showModal : true
     })
    };

     handleClose = () => {
      this.setState({
        showModal : false
      }) 
    };

    handleSubmit = () => {
      const {name, type, price, rating, years } = this.state
      const sendData = {name: name , type: type , price: price , rating: rating , years: years } 
      axios
      .post("http://localhost:3000/product" , sendData )
      .then(response => {
        console.log(response , 'data send');
      })
      .catch(error => console.log(error));
    }

    render() {
        const { name, type, price, rating, years, available , showModal } = this.state
        return (
        <div style={style.modal}>
          <Button type="button" onClick={this.handleOpen}>
           Création nouveau produit
          </Button>
          <Modal style = {{width : 500}}
            aria-labelledby="simple-modal-title"
            aria-describedby="simple-modal-description"
            open={showModal}
            >
            <div>
              <form  noValidate autoComplete="off">
              <TextField
                name='name'
                id="standard-name"
                label="Model"
                value={name}
                onChange={this.handleChange}
                margin="normal"
              />
              <TextField
                id="standard-name"
                name='type'
                label="type"
                value={type}
                onChange={this.handleChange}
                margin="normal"
              />
              <TextField
              id="standard-number"
              name ='price'
              label="Price"
              value={price}
              onChange={this.handleChange}
              type="number"
              InputLabelProps={{
              shrink: true,
              }}
              margin="normal"
              />
              <TextField
              id="standard-number"
              name ='rating'
              label="Rating"
              value={rating}
              onChange={this.handleChange}
              type="number"
              InputLabelProps={{
              shrink: true,
              }}
              margin="normal"
              />
              <TextField
              id="standard-number"
              name ='years'
              label="Years"
              value={years}
              onChange={this.handleChange}
              type="number"
              InputLabelProps={{
              shrink: true,
              }}
              margin="normal"
              />
              </form>
              <Button style ={style.button} onClick = {this.handleClose}>Close</Button>
              <Button style ={style.button} onClick = {this.handleSubmit}>Send</Button>
            </div>
          </Modal>
        </div>

          );
        }        
    }

    const style = {

    modal : {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: 'rgb(247, 143, 63)',
    color: 'white',

    height : '800px'
      },

    button: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      backgroundColor: 'rgb(247, 143, 63)',
      color : 'red',
      width : '100%'
      },
    }
