import React, { Component } from 'react';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import axios from 'axios';

export default class Connection extends Component{
    constructor(props){
        super(props);
        this.state = {
            email : '',
            password :'',
        }
    }

    handleChange = (e) => {
        e.preventDefault();
        this.setState({
          [e.target.name]: e.target.value
        })
      }

    handleSubmit = (e) =>{
        axios
        .post(`http://localhost:3000/connexion/connexion?email=${this.state.email}&password=${this.state.password}`)
        .then(function(res,err){
            return res.json()
        }).then(function(data){
            console.log(data)
        }).catch(function(err){
            console.log(err)
        })

        e.preventDefault();
        console.log('SUBMIT++++++++++++++', this.state)

    }
    render(){

        return(
            <div>
                <h1>Connection</h1>
                <TextField
                name='email'
                id="standard-name"
                label="Email"
                value={this.state.email}
                onChange={this.handleChange}
                margin="normal"
                />
                  <TextField
                    id="standard-password-input"
                    label="Password"
                    value = {this.state.password}
                    onChange={this.handleChange}
                    type="password"
                    margin="normal"
                /> 
                 <Button size="large" color="primary" onClick = {this.handleSubmit}>Connection</Button>
            </div>
        );
    }
}
