import React, { Component } from 'react';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';

export default class inscription extends Component{
       constructor(props){
        super(props);
        this.state = {
            lastName : '',
            firstName:'',
            email : '',
            lastNameError:'',
            firstNameError :'',
            emailError:'',
        }
    }

    validate = () => { 
    let lastNameError = '';
    let firstNameError = '';
    let emailError = '';


    if(!this.state.email.includes('@')){
        emailError = 'Saisissez une adresse électronique valide.' 
    }
    if(this.state.lastName < 3 ){
        lastNameError = 'Ce champ est obligatoire : Au moins 3 caractères'
    }
    if(this.state.firstName < 3 ){
        firstNameError  = 'Ce champ est obligatoire : Au moins 3 caractères'
    }
    if(emailError || lastNameError || firstNameError  ){
        this.setState({ emailError , firstNameError , lastNameError });
        return false
        }

    return ;
    } 
    handleChange = (e) => {
        e.preventDefault();
        this.setState({
          [e.target.name]: e.target.value
        })
      }

    handleSubmit = (e) => {
        var signupData = JSON.stringify({
            Firstname : this.state.firstName,
            Lastname:this.state.lastName,
            // password : this.state.password,
            email : this.state.email,
        });
        console.log('signuppppp',signupData);

        fetch('http://localhost:3000/inscription/inscription',{
            method : 'POST',
            headers : {'Content-Type': 'application/json'},
            body : signupData,
        }).then(function(res, err){
            return res.json()
        }).then(function(data){
            console.log(data)
        }).catch(function(err){
            console.log(err)
        })
        e.preventDefault();
        const isValid = this.validate();
        if(isValid){
            this.setState({
                isValid
            })
        }
        console.log('SUBMIT++++++++++++++', this.state)
    }

    render(){
        const { firstName, lastName , email} = this.state
        return(
            <div>
                <form noValidate autoComplete="off">
                <TextField
                name='firstName'
                id="standard-name"
                label="LastName"
                value={firstName}
                onChange={this.handleChange}
                margin="normal"
                />
                <TextField
                name='lastName'
                id="standard-name"
                label="FirstName"
                value={lastName}
                onChange={this.handleChange}
                margin="normal"
                />
                {/* <TextField
                    id="standard-password-input"
                    label="Password"
                    value = {password}
                    onChange={this.handleChange}
                    type="password"
                    margin="normal"
                /> */}
                <TextField
                name='email'
                id="standard-name"
                label="Email"
                value={email}
                onChange={this.handleChange}
                margin="normal"
                />
                </form>
                <Button size="large" color="primary" onClick = {this.handleSubmit}>Send</Button>
            </div>
        );
    }
}
