import React from 'react';
import TextField from '@material-ui/core/TextField';
import Modal from '@material-ui/core/Modal';

export default class SimpleModal extends React.Component {
    constructor(props){ 
        super(props) 
        this.state = {
        name : '',
        type : '',
        price: '',
        rating : '',
        years : '',
        available : false,
        open : false,
     }
    }
    handleChange(e){
      console.log(e.target.name)
      e.preventDefault();
      this.setState({
        [e.target.name]: e.target.value
      })
    }

     handleOpen = () => {
     this.setState({
       open : true
     })
    };

     handleClose = () => {
      this.setState({
        open: false
      })
    };

    render() {
        const { name, type, price, rating, years, available , open } = this.state
        return (
        <div>
          <button type="button" onClick={this.handleOpen}>
           Cr√©ation nouveau produit
          </button>
          <Modal
            aria-labelledby="simple-modal-title"
            aria-describedby="simple-modal-description"
            open={open}
            >
            <div>
              <form noValidate autoComplete="off">
              <TextField
                name='name'
                id="standard-name"
                label="Model"
                value={name}
                onChange={this.handleChange}
                margin="normal"
              />
              <TextField
                id="standard-name"
                name='type'
                label="type"
                value={type}
                onChange={this.handleChange}
                margin="normal"
              />
              <TextField
              id="standard-number"
              name ='price'
              label="Price"
              value={price}
              onChange={this.handleChange}
              type="number"
              InputLabelProps={{
              shrink: true,
              }}
              margin="normal"
              />
              <TextField
              id="standard-number"
              name ='rating'
              label="Rating"
              value={rating}
              onChange={this.handleChange}
              type="number"
              InputLabelProps={{
              shrink: true,
              }}
              margin="normal"
              />
              <TextField
              id="standard-number"
              name ='years'
              label="Years"
              value={years}
              onChange={this.handleChange}
              type="number"
              InputLabelProps={{
              shrink: true,
              }}
              margin="normal"
              />
              </form>
              <button onClick = {this.handleClose}>X</button>
              <SimpleModal />
            </div>
          </Modal>
        </div>

          );
        }        
    }
